const uint8_t *SoundPtr;
 uint32_t length;
void SysTick_IntArm(uint32_t period, uint32_t priority){
  // write this
    SysTick->CTRL = 0; // 1)
            //disable SysTick during setup
        SysTick->LOAD = (period-1); // 2)
            //stops
        SysTick->VAL = 0; // 3)
        SCB->SHP[1] = (SCB->SHP[1]&(~0xC0000000))| priority<<30; //straight from the textbook, don't think i need to edit
        //SysTick->VAL = 0; // 3)
            //any write to current clears it
        SysTick->CTRL = 0x0007; // 4)
}

// initialize a 11kHz SysTick, however no sound should be started
// initialize any global variables
// Initialize the 5 bit DAC
void Sound_Init(void){
            DAC5_Init();
            SysTick_IntArm((80000000/11000),2);
            SysTick->CTRL = 0; // 1)
// write this

}
void SysTick_Handler(void){ // called at 11 kHz
  // output one value to DAC if a sound is active
    if (length){
    length--;
    DAC5_Out(*SoundPtr);
    SoundPtr++;}
    else{  SysTick->CTRL = 0;
}}

//******* Sound_Start ************
// This function does not output to the DAC.
// Rather, it sets a SoundPtr and counter, and then enables the SysTick interrupt.
// It starts the sound, and the SysTick ISR does the output
// feel free to change the parameters
// Sound should play once and stop
// Input: pt is a SoundPtr to an array of DAC outputs
//        count is the length of the array
// Output: none
// special cases: as you wish to implement


void Sound_Start(const uint8_t *pt, uint32_t count){
// write this

SoundPtr=pt;
length=count;
 SysTick->VAL = 1;           //
 SysTick->CTRL=0x07;//reenables systick to start


}
void Sound_PINGPONGHIT(void){

   Sound_Start (pingpongballhit, 5006);
}
void Sound_POINT(void){

       Sound_Start (soundpoint, 8704);
}
void Sound_WALL(void){


    Sound_Start (WALLSOUND, 4096);
}

